  // console.log(array)
  // console.log(array.length) 
  let elements=""
let className="divMain"
  const addProduct=(item)=>{
  // console.log(item)
  addProducts(item)
  }

  
  if(array.length>0){
     
       elements=array.map(item=>{
        //   console.log(item)
          return(
            <ProductItem obj={item} key={item.id} purchasedItem={addProduct}/>
          )
      })
  }else{elements=""}
    return(
        <div className={className}>
            <h1>{list}</h1>
            {elements}  
        </div>
    )
}
export default Main
