          return(
            <ProductItem obj={item} key={item.id+index} purchasedItem={addProduct}/>
          )
      })
  }
  else{elements=""}}
  // console.log(search)
 
    return(
        <div className={className}>
            <h1>{topic}</h1>
            {elements}  
        </div>
    )
}
export default Main
