import { useState } from 'react/cjs/react.development'
import './basket.css'
const Basket=({style,itemsArray})=>{
    let className="basketProduct"
    let classForProduct="products"
if(style){
    className+=" hide"
}
const [price,setPrice]=useState(0)
let currentPrice=+0
const elements=itemsArray.map(item=>{
    currentPrice+=+item.price
    
    const {icon,...itemProps}=item
          return (
        <div className={classForProduct} key={item.id}>
            <div className="flex">
               <img src={process.env.PUBLIC_URL + 'img/'+item.id+`.jpg`} alt="icon"/>
            </div>
            <div>
                  <div>{item.price}</div>
                  <div>{item.model}</div>
            </div> 
        </div>
    )
})
    return(
        <div className={className}>
           <div> {elements}</div>
           <div className="buy">
               <div>Всього до сплати: <span className="price">{currentPrice}$</span></div>
               <button className="btn">Купити</button>
           </div>
        </div>
    )
}
export default Basket
