import React, { useEffect, useRef, useState } from 'react'
import './header.css'
import logo from './Img/logo_n.svg'
import basket from './Img/basket.png'

const Header=({products,itemsArray})=>{
    let counter=useRef(null)
        console.log(counter.current.innerText )
        const showProducts=()=>{
            console.log(itemsArray)
        }
    return(
        <>
    <div  className="header flex">
       <img src={logo} alt="title" className="headerImg"/>
       <div className="search flex">
            <input type="search" placeholder="Я шукаю..." results="0"/>
            <button>Знайти</button>
        </div >
        <div>
            <img src={basket} 
                 alt="basket" 
                 onClick={showProducts}
                 className="basket"/>
            <span ref={counter}>{itemsArray.length}</span>
        </div>
    </div>
        </>
    )
}
export default Header
