        <div>
          <Header products={selectedProducts} 
                  itemsArray={productList}
                  purchased={purchased}
                  hideAll={hideAll}
                  search={search}
                  filter={(e)=>filter(e)}/>
          <TopicList list={allProducts.list} 
                     selected={(category)=>choseCategory(category)}/>
          <Main list={SelectedGroup}
                products={showProducts}
                addProducts={addProductToArray}
                hide={hide}
                search={search}/>
